mapper.select:

select dog_id, dog_name, dog_age, dog_weight, dog_breed, dog_bloodtype, name, email, phone, apply_status, blood_cc, dog_image, description, donation_date 
from (select rownum rn, tt.*from(select*from apply_dogiver join dogiver using(dog_id) join member using(email) order by dog_id desc)tt) 
where rn>=#{startNum} and rn<=#{endNum}
